package com.inhelp.diimport android.app.Applicationimport com.inhelp.MainAppimport com.inhelp.di.modules.ActivityBindingModuleimport com.inhelp.di.modules.ApplicationModuleimport com.inhelp.di.modules.BaseModuleimport dagger.BindsInstanceimport dagger.Componentimport dagger.android.AndroidInjectorimport dagger.android.support.AndroidSupportInjectionModuleimport javax.inject.Singleton@Singleton@Component(modules = [    (ApplicationModule::class),    (BaseModule::class),    (ActivityBindingModule::class),    (AndroidSupportInjectionModule::class)])interface AppComponent : AndroidInjector<MainApp> {    @Component.Builder    interface Builder {        @BindsInstance        fun application(application: Application): AppComponent.Builder        fun build(): AppComponent    }}