package com.inhelp.view.mainimport android.os.Bundleimport com.inhelp.Rimport com.inhelp.view.mvp.BaseMvpActivityimport javax.inject.Injectclass MainActivity : BaseMvpActivity<MainView, MainPresenter>(), MainView {    @Inject    override    lateinit var presenter: MainPresenter    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        presenter.openFragment(intent.getIntExtra("fragment", 0))    }    override fun backPress() {        presenter.backPress()    }    override fun onBackPressed() {        backPress()    }    override fun onResume() {        super.onResume()    }}