package com.inhelp.view.mainimport android.os.Bundleimport com.inhelp.Rimport com.inhelp.di.DaggerAppComponentimport com.inhelp.view.mvp.BaseMvpActivityimport javax.inject.Injectclass MainActivity : BaseMvpActivity<MainView, MainPresenter>(), MainView {    @Inject    override    lateinit var presenter: MainPresenter    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)    }    override fun backPress() {        presenter.backPress()    }    override fun onBackPressed() {        backPress()    }}